export const Button = ({ children, onClick }) => (
  <button onClick={onClick}>{children}</button>
);

export const ProductCard = ({ name, price }) => (
  <div className='card'>
    <h4>{name}</h4>
    <p>{price} ₽</p>
    <Button>Купить</Button>
  </div>
);

export const ProductList = ({ products }) => (
  <div className='list'>
    {products.map(p => <ProductCard key={p.id} name={p.name} price={p.price} />)}
  </div>
);

export const CatalogPage = ({ products }) => (
  <section>
    <h2>Каталог</h2>
    <ProductList products={products} />
  </section>
);

export const Layout = ({ children }) => (
  <div>
    <header>Магазин</header>
    <main>{children}</main>
    <footer>© 2025</footer>
  </div>
);

export const App = () => {
  const products = [
    { id: 1, name: 'Ноутбук', price: 90000 },
    { id: 2, name: 'Смартфон', price: 60000 }
  ];

  return (
    <Layout>
      <CatalogPage products={products} />
    </Layout>
  );
};
