export const DiscountPrice = (props) => {
  const discount = 0.2;
  return (
    <div>
      Цена со скидкой: {props.price - props.price * discount}
    </div>
  );
};

export const DiscountPrice = ({ price }) => {
  const discount = 0.2;
  return (
    <div>
      Цена со скидкой: {price - price * discount}
    </div>
  );
};

export const Temperature = ({ celsius }) => {
  const factor = 9/5;
  const offset = 32;
  return (
    <div>
      {celsius} °C = {celsius * factor + offset} °F
    </div>
  );
};

