export const DynamicList = ({ items }) => (
  <ul>
    {items.map(({ id, title, content }) => (
      <li key={id}>
        <h4>{title}</h4>
        <p>{content}</p>
      </li>
    ))}
  </ul>
);

export const LayoutPage = ({ header, footer, children }) => (
  <div className="layout">
    <header>{header}</header>
    <main>{children}</main>
    <footer>{footer}</footer>
  </div>
);

export default function Dashboard({ HeaderComponent, FooterComponent }) {
  const items = [
    { id: 'a1', title: 'Элемент 1', content: 'Содержимое первого элемента' },
    { id: 'a2', title: 'Элемент 2', content: 'Содержимое второго элемента' },
    { id: 'a3', title: 'Элемент 3', content: 'Содержимое третьего элемента' }
  ];

  return (
    <LayoutPage
      header={<HeaderComponent title="Заголовок Dashboard" />}
      footer={<FooterComponent text="Футер Dashboard" />}
    >
      <Container>
        <div>Первый дочерний блок</div>
        <div>Второй дочерний блок</div>
        <DynamicList items={items} />
      </Container>
    </LayoutPage>
  );
}

