import React from 'react';
import { createBrowserRouter } from 'react-router-dom';
import { AppLayout } from './layout/AppLayout';
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import { NotFoundPage } from './pages/NotFoundPage';

export const router = createBrowserRouter([
  {
    path: '/',
    element: <AppLayout />,
    children: [
      { index: true, element: <HomePage /> },
      { path: 'about', element: <AboutPage /> }
    ],
    errorElement: <NotFoundPage />
  }
]);

import React from 'react';
import { Outlet, NavLink, useLocation } from 'react-router-dom';

export const AppLayout = () => {
  const location = useLocation();
  const title = location.pathname === '/about' ? 'О проекте' : 'Главная страница';

  return (
    <div>
      <header>
        <h2>{title}</h2>
        <nav>
          <NavLink to='/' end>Главная</NavLink>{' | '}
          <NavLink to='/about'>О проекте</NavLink>
        </nav>
      </header>
      <main>
        <Outlet />
      </main>
    </div>
  );
};

import React from 'react';

export default function HomePage() {
  return (
    <section>
      <p>Добро пожаловать на главную страницу! Здесь начинается ваше знакомство с приложением.</p>
    </section>
  );
}

import React from 'react';

export default function AboutPage() {
  return (
    <section>
      <p>Это приложение демонстрирует базовую работу с лэйаутом и маршрутизацией в React Router.</p>
    </section>
  );
}
